// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.4;
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IArtifact.sol";
import "./ICertification.sol";

contract MarketX is Ownable{

    mapping(address => uint) private STAKED;
    mapping(address => uint) private WITHDRAWABLE;
    address private CERTIFICATION_REGISRTY;

    event ArtifactStaked(address _artifactAddress, address _staker);
    event ArtifactTransferred(address _artifactAddress, address _seller, address _buyer);
    event WithdrawFunds(address _address, uint _amount);
    event ArtifactMinted(address owner, address tokenContract); //emmited when ERC1155 token is minted
    event ArtifactExamined(address _artifactAddress, bool status);
    event ArtifactSold(address _artifactAddress, address _from, address _to);

    constructor(address certificationRegistry) public{
        CERTIFICATION_REGISRTY = certificationRegistry;
    }

    modifier onlyArtifactOwner(address _artifactAddress) {
        require(msg.sender == IArtifact(_artifactAddress).getHodler());
        _;
    }

    modifier onlyWhitelisted(){
        require(ICertification(CERTIFICATION_REGISRTY).isWhitelisted(msg.sender)==true,"MARKETX: This address is not ACM");
        _;
    }

    function mintArtifact(address _artifactAddress) public payable {
        require(IArtifact(_artifactAddress).getPrice()==msg.value,"ArtiFactory: Insuffiencent Ether");
        IArtifact(_artifactAddress).mint(msg.sender);
        emit ArtifactMinted(IArtifact(_artifactAddress).getHodler(), _artifactAddress);
    }

    function buy(address _artifactAddress) public payable {
        require(IArtifact(_artifactAddress).getStatus() == IArtifact.Status.Verified,"MarketX: Artifact not ready for sale yet");
        require(IArtifact(_artifactAddress).getPrice() == msg.value,"MarketX: Insufficient Ether");
        address hodler = IArtifact(_artifactAddress).getHodler();
        IArtifact(_artifactAddress).transfer(msg.sender);
        emit ArtifactSold(_artifactAddress,hodler,msg.sender);
    }

    function verify(address _artifactAddress, bool _authentic) public onlyWhitelisted {
        require(IArtifact(_artifactAddress).getStatus() == IArtifact.Status.Staked,"MarketX: Artifact is not staked");
        require(msg.sender == IArtifact(_artifactAddress).getCreator(),"MarketX: You are not the creator of the Artifact");
        address hodler = IArtifact(_artifactAddress).getHodler();
        uint price = IArtifact(_artifactAddress).getPrice();
        if(_authentic){
            IArtifact(_artifactAddress).setStatus(IArtifact.Status.Verified);
            WITHDRAWABLE[hodler] = STAKED[hodler]+price;
            STAKED[hodler] -= price;
        } else {
            IArtifact(_artifactAddress).setStatus(IArtifact.Status.Hodl); 
            STAKED[hodler] -= price;  
        }
        emit ArtifactExamined(_artifactAddress,_authentic);
    }

    
    function stake(address _artifactAddress) public onlyArtifactOwner(_artifactAddress) payable {
        require(msg.value == IArtifact(_artifactAddress).getPrice(), "MarketX: Stake amount equal to Artifact price");
        require(IArtifact(_artifactAddress).isApprovedForAll(msg.sender,address(this)),"Marketx: Grant MarketX permission to stake Artifact");
        STAKED[msg.sender] = msg.value;
        IArtifact(_artifactAddress).setStatus(IArtifact.Status.Staked);
        emit ArtifactStaked(_artifactAddress,msg.sender);
    }
   

    function withdraw() public {
        require(WITHDRAWABLE[msg.sender]>0, "MARKETX: No amount to withdraw!");   
        uint value = WITHDRAWABLE[msg.sender];
        WITHDRAWABLE[msg.sender] = 0;
        payable(msg.sender).transfer(value);
        emit WithdrawFunds(msg.sender,value);
    }

}
